package day3;

public class FunctionDemo {
    public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello");
		addition();

	}

	public static void addition() {

		int a = 7;
		int b = 8;
		int c = a + b;
		sub();
		System.out.println(c);

	}

	public static void sub() {

		int a = 7;
		int b = 8;
		int c = a - b;
		System.out.println(c);

	}
}
